  // MARK: Navigation
  
  func navigateToPicker(source: CheckCarViewController, destination: DateTimeViewController)
  {
    source.show(destination, sender: nil)
  }
  
  // MARK: Passing data
  
  func passDataToPicker(source: CheckCarDataStore, destination: inout DateTimeDataStore)
  {
    let date = source.currentDate ?? Date()
    destination.currentDate = date
  }
}
