        let validMake = selectMake != nil && !(selectMake?.isEmpty ?? false)
        let validBody = selectBody != nil && !(selectBody?.isEmpty ?? false)
        
        let validModelCode = modelCode != nil && !(modelCode?.isEmpty ?? false)
        
