        let validBuildYear = buildYear != nil && !(buildYear?.isEmpty ?? false) && (buildYear?.isValidNumber() ?? false)
        
        var message : String = ""
        message += validChassisno ? "" : "Chassis No. à¹„à¸¡à¹ˆà¸–à¸¹à¸à¸•à¹‰à¸­à¸‡\n"
