    fileprivate func getYears(from starterYear: Int) -> [String] {
        let df = DateFormatter()
        let years = df.years(starterYear...Date().year)
            .sorted(by: { $0 > $1 })
            //.map({ $0.toInt() + 543 })
            //.map({ $0.toString() })
            
        return years
    }
    
    
    
    
    
