import UIKit

protocol DetailBusinessLogic
{
    func showDetail(request: Detail.Model.Request)
}

protocol DetailDataStore
{
  //var name: String { get set }
    var modelBookinList : BookInListResponse? { get set }
}

class DetailInteractor: DetailBusinessLogic, DetailDataStore
{
  var presenter: DetailPresentationLogic?
  var worker: DetailWorker?
  //var name: String = ""
  
  // MARK: Do something
    var modelBookinList : BookInListResponse?
  
  func showDetail(request: Detail.Model.Request)
  {
    let response = Detail.Model.Response(item: modelBookinList)
    presenter?.presentDetail(response: response)
  }
}
