import UIKit

protocol GradeCarPresentationLogic
{
  func presentSomething(response: GradeCar.Something.Response)
    func presentGradeResult(response: GradeCar.Something.Response)
}

class GradeCarPresenter: GradeCarPresentationLogic
{
  weak var viewController: GradeCarDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: GradeCar.Something.Response)
  {
    let viewModel = GradeCar.Something.ViewModel()
    viewController?.displaySomething(viewModel: viewModel)
  }
    
    func presentGradeResult(response: GradeCar.Something.Response) {
        
        let viewModel = GradeCar.Something.ViewModel(grade: response.grade)
        viewController?.displayGradeResult(viewModel: viewModel)
    }
}
