import UIKit

protocol LandingPageBusinessLogic
{
  func fetchStandardMake(request: LandingPage.Something.Request)
}

protocol LandingPageDataStore
{
  //var name: String { get set }
}

class LandingPageInteractor: LandingPageBusinessLogic, LandingPageDataStore
{
  var presenter: LandingPagePresentationLogic?
  var worker: LandingPageWorker?
  //var name: String = ""
  
  // MARK: Do something
  
  func fetchStandardMake(request: LandingPage.Something.Request)
  {
    worker = LandingPageWorker()
    worker?.fetchStandardMakeList(completion: { [weak self] (response) in
        self?.presenter?.presentStandardMake(response: response)
        
        if let makeList = response.standardMakeList {
            DataController.shared.standardMakeList = makeList
        }
    })
  }
}
