import UIKit

protocol LandingPagePresentationLogic
{
  func presentStandardMake(response: LandingPage.Something.Response)

}

class LandingPagePresenter: LandingPagePresentationLogic
{
  weak var viewController: LandingPageDisplayLogic?
  
  // MARK: Do something
  
  func presentStandardMake(response: LandingPage.Something.Response)
  {
    if let error = response.error {
        let errorMessage = error.message
        let viewModel = LandingPage.Something.ViewModel(errorMessage: errorMessage)
        viewController?.displayErrorMessage(viewModel: viewModel)
    }else{
        let viewModel = LandingPage.Something.ViewModel()
        viewController?.displaySomething(viewModel: viewModel)
    }
  }
   
}
