import UIKit

typealias standardMakeHandler = (LandingPage.Something.Response) -> ()

class LandingPageWorker
{
  
    func fetchStandardMakeList(completion: @escaping standardMakeHandler){
        
        showLoading()
        
        let request = BaseRequest()
        StandardMakeService().callServiceArray(request: request) { (result) in
            
            hideLoading()
            
            switch result {
            case .success(let standardMakeList):
                let response = LandingPage.Something.Response(standardMakeList: standardMakeList)
                completion(response)
            case .failure(let error):
                let response = LandingPage.Something.Response(error: error.getMessage)
                completion(response)
            }
        }
    }
}
