import UIKit

@objc protocol ItemAllListRoutingLogic
{
  func routeTosearchByPicker(segue: UIStoryboardSegue?)
    func routeTodescriptionSegue(segue: UIStoryboardSegue?)
    func routeToViewPDF(segue: UIStoryboardSegue?)
    func routeToEditView(segue: UIStoryboardSegue?)
}

protocol ItemAllListDataPassing
{
  var dataStore: ItemAllListDataStore? { get }
}

class ItemAllListRouter: NSObject, ItemAllListRoutingLogic, ItemAllListDataPassing
{
  weak var viewController: ItemAllListViewController?
  var dataStore: ItemAllListDataStore?
  
  // MARK: Routing
  
   
  func routeTosearchByPicker(segue: UIStoryboardSegue?)
  {
    if let segue = segue {
      let destinationVC = segue.destination as! SearchByViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToSearchBy(source: dataStore!, destination: &destinationDS)
    } else {
      let storyboard = UIStoryboard(name: "Main", bundle: nil)
      let destinationVC = storyboard.instantiateViewController(withIdentifier: "SearchByViewController") as! SearchByViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToSearchBy(source: dataStore!, destination: &destinationDS)
      navigateToSearchBy(source: viewController!, destination: destinationVC)
    }
  }
    
    func routeTodescriptionSegue(segue: UIStoryboardSegue?)
    {
      if let segue = segue {
        let destinationVC = segue.destination as! DetailViewController
        var destinationDS = destinationVC.router!.dataStore!
        passDataToDetail(source: dataStore!, destination: &destinationDS)
      } else {
        let storyboard = UIStoryboard(name: "Main", bundle: nil)
        let destinationVC = storyboard.instantiateViewController(withIdentifier: "DetailViewController") as! DetailViewController
        var destinationDS = destinationVC.router!.dataStore!
        passDataToDetail(source: dataStore!, destination: &destinationDS)
        navigateToDetail(source: viewController!, destination: destinationVC)
      }
    }

    func routeToViewPDF(segue: UIStoryboardSegue?) {
        if let segue = segue {
          let destinationVC = segue.destination as! PDFPreviewViewController
          var destinationDS = destinationVC.router!.dataStore!
            passDataToPdfViewer(source: dataStore!, destination: &destinationDS)
        } else {
          let storyboard = UIStoryboard(name: "PDF", bundle: nil)
          let destinationVC = storyboard.instantiateViewController(withIdentifier: "PDFPreviewViewController") as! PDFPreviewViewController
          var destinationDS = destinationVC.router!.dataStore!
            passDataToPdfViewer(source: dataStore!, destination: &destinationDS)
            navigateToPdfViewer(source: viewController!, destination: destinationVC)
        }
    }
    
    func routeToEditView(segue: UIStoryboardSegue?) {
        if let segue = segue {
          let destinationVC = segue.destination as! PickUpCarViewController
          var destinationDS = destinationVC.router!.dataStore!
            passDataToPickUpCar(source: dataStore!, destination: &destinationDS)
        } else {
          let storyboard = UIStoryboard(name: "PickUpCar", bundle: nil)
          let destinationVC = storyboard.instantiateViewController(withIdentifier: "PickUpCarViewController") as! PickUpCarViewController
          var destinationDS = destinationVC.router!.dataStore!
            passDataToPickUpCar(source: dataStore!, destination: &destinationDS)
            navigateToPickUpCar(source: viewController!, destination: destinationVC)
        }
    }
    
  // MARK: Navigation
  
    func navigateToSearchBy(source: ItemAllListViewController, destination: SearchByViewController){
        source.show(destination, sender: nil)
    }
    
    func navigateToDetail(source: ItemAllListViewController, destination: DetailViewController){
      source.show(destination, sender: nil)
    }
    
    func navigateToPdfViewer(source: ItemAllListViewController, destination: PDFPreviewViewController){
      source.show(destination, sender: nil)
    }
    
    func navigateToPickUpCar(source: ItemAllListViewController, destination: PickUpCarViewController){
      source.show(destination, sender: nil)
    }
  
  // MARK: Passing data
  
    func passDataToSearchBy(source: ItemAllListDataStore, destination: inout SearchByDataStore){
        destination.searchBy = source.searchBy
    }
    
    func passDataToDetail(source: ItemAllListDataStore, destination: inout DetailDataStore){
        destination.modelBookinList = source.modelBookinList
    }
    
    func passDataToPdfViewer(source: ItemAllListDataStore, destination: inout PDFPreviewDataStore){
        destination.documentData = source.documentData
        destination.pdfName = source.pdfName
    }
    func passDataToPickUpCar(source: ItemAllListDataStore, destination: inout PickUpCarDataStore){
        //
        
    }
}
