    // MARK: Passing data
    
      
      func passDataToDetail(source: ItemPendingListDataStore, destination: inout DetailDataStore)
      {
        destination.modelBookinList = source.modelBookinList
      }
      
      func passDataToPdfViewer(source: ItemPendingListDataStore, destination: inout PDFPreviewDataStore)
      {
          destination.documentData = source.documentData
        destination.pdfName = source.pdfName
      }
    
