import UIKit

protocol SearchByBusinessLogic
{
  func fetchDataPicker(request: SearchBy.Picker.Request)
    func selectPicker(request: SearchBy.Picker.Request)
}

protocol SearchByDataStore
{
  //var name: String { get set }
    var searchBy : SearchBy.TYPE? { get set }
}

class SearchByInteractor: SearchByBusinessLogic, SearchByDataStore
{
    var searchBy: SearchBy.TYPE?
    
  var presenter: SearchByPresentationLogic?
  var worker: SearchByWorker?
  //var name: String = ""
  
  // MARK: Do something
    private var searchList = [String.localized("inspection_list_car_registration_label"), "VIN", String.localized("car_detail_engine_number_label")]
  

  func fetchDataPicker(request: SearchBy.Picker.Request)
  {
    let response = SearchBy.Picker.Response(searchByList: searchList, selectSearchByName: searchBy)
    presenter?.presentDisplayPicker(response: response)
  }
   
    func selectPicker(request: SearchBy.Picker.Request) {
        
        let selectSearchByName =  getSearchTypeModel(from: request.selectRow ?? 0)
        let response = SearchBy.Picker.Response(selectSearchByName: selectSearchByName)
        
        presenter?.selectSearchType(response: response)
    }
    
    func getSearchTypeModel(from row : Int) -> SearchBy.TYPE {
        switch searchList[row] {
        case String.localized("inspection_list_car_registration_label") :
            return .REGISTRATION_CAR
        case "VIN" :
            return .VIN
        case String.localized("car_detail_engine_number_label"):
            return .ENGINE_NUMBER

        default:
            return .REGISTRATION_CAR
        }
    }
}
