import UIKit

enum SearchBy
{
  // MARK: Use cases
    enum TYPE : Int {
        case REGISTRATION_CAR
        case VIN
        case ENGINE_NUMBER

        
        var model : (Int, String, String) {
            switch self {
            case .REGISTRATION_CAR:
                return (0, String.localized("inspection_list_car_registration_label"), "registration")
            case .VIN:
                return (1, "VIN", "vin")
            case .ENGINE_NUMBER:
                return (2, String.localized("car_detail_engine_number_label"), "engineno")

            }
        }
    }
  enum Picker
  {
    struct Request
    {
        var selectRow : Int?
    }
    struct Response
    {
        var searchByList : [String]?
        var selectSearchByName : TYPE?
    }
    struct ViewModel
    {
        var searchByList : [String]?
        var selectSearchByName : TYPE?
    }
  }
}
