import UIKit

protocol SearchByPresentationLogic
{
  func presentDisplayPicker(response: SearchBy.Picker.Response)
    func selectSearchType(response: SearchBy.Picker.Response)
}

class SearchByPresenter: SearchByPresentationLogic
{
  weak var viewController: SearchByDisplayLogic?
  
  // MARK: Do something
  
  func presentDisplayPicker(response: SearchBy.Picker.Response)
  {
    let viewModel = SearchBy.Picker.ViewModel(searchByList: response.searchByList, selectSearchByName: response.selectSearchByName)
    viewController?.presentUpdatePicker(viewModel: viewModel)
  }
    func selectSearchType(response: SearchBy.Picker.Response) {
        let viewModel = SearchBy.Picker.ViewModel(selectSearchByName: response.selectSearchByName)
        viewController?.selectSearchByPicker(viewModel: viewModel)
    }
}
