import UIKit

protocol LoginPresentationLogic
{
 
    func presentValidateField(response: Login.getLogin.Response)
    func presetResultLogin(response: Login.getLogin.Response)
}

class LoginPresenter: LoginPresentationLogic
{
  weak var viewController: LoginDisplayLogic?
  
  // MARK: Do something
  
    func presentValidateField(response: Login.getLogin.Response) {
        guard let errorMessage  = response.validateErrorMessage else { return }
        if errorMessage.trimWhiteSpace.isEmpty {

            let viewModel = Login.getLogin.ViewModel()
            viewController?.displayValidateSuccess(viewModel: viewModel)
        }else{
            let viewModel = Login.getLogin.ViewModel(errorMessage: errorMessage)
            viewController?.displayValidateErrorMessage(viewModel: viewModel)
        }
    }
    
    func presetResultLogin(response: Login.getLogin.Response) {
        if let error = response.error {
            let viewModel = Login.getLogin.ViewModel(errorMessage: error.message)
            viewController?.displayErrorLogin(viewModel: viewModel)
        }else{
            let viewModel = Login.getLogin.ViewModel()
            viewController?.displayResultLogin(viewModel: viewModel)
        }
    }
}
