import UIKit

protocol PDFPreviewPresentationLogic
{
  func presentPDFViewer(response: PDFPreview.Something.Response)
    func presentPrint(response: PDFPreview.Something.Response)
}

class PDFPreviewPresenter: PDFPreviewPresentationLogic
{
  weak var viewController: PDFPreviewDisplayLogic?
  
  // MARK: Do something
  
  func presentPDFViewer(response: PDFPreview.Something.Response)
  {
    let viewModel = PDFPreview.Something.ViewModel(documentData: response.documentData)
    viewController?.displayPDF(viewModel: viewModel)
  }
    func presentPrint(response: PDFPreview.Something.Response) {
        let viewModel = PDFPreview.Something.ViewModel(documentData: response.documentData,
                                                       pdfName: response.pdfName)
        viewController?.printPDF(viewModel: viewModel)
    }
}
