import UIKit

@objc protocol CabinCarRoutingLogic
{
  func routeToshowDateTime(segue: UIStoryboardSegue?)
}

protocol CabinCarDataPassing
{
  var dataStore: CabinCarDataStore? { get }
}

class CabinCarRouter: NSObject, CabinCarRoutingLogic, CabinCarDataPassing
{
  weak var viewController: CabinCarViewController?
  var dataStore: CabinCarDataStore?
  
  // MARK: Routing
  
  func routeToshowDateTime(segue: UIStoryboardSegue?)
  {
    if let segue = segue {
      let destinationVC = segue.destination as! DateTimeViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToSomewhere(source: dataStore!, destination: &destinationDS)
    } else {
      let storyboard = UIStoryboard(name: "DateTime", bundle: nil)
      let destinationVC = storyboard.instantiateViewController(withIdentifier: "DateTimeViewController") as! DateTimeViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToSomewhere(source: dataStore!, destination: &destinationDS)
      navigateToSomewhere(source: viewController!, destination: destinationVC)
    }
  }

  // MARK: Navigation
  
  func navigateToSomewhere(source: CabinCarViewController, destination: DateTimeViewController)
  {
    source.show(destination, sender: nil)
  }
  
  // MARK: Passing data
  
  func passDataToSomewhere(source: CabinCarDataStore, destination: inout DateTimeDataStore)
  {
    let date = source.currentDate ?? Date()
    destination.currentDate = date
  }
}
