import UIKit

@objc protocol PickUpCarRoutingLogic
{
  func routeToshowDateTime(segue: UIStoryboardSegue?)
    func routeTopdfviewer(segue: UIStoryboardSegue?)
}

protocol PickUpCarDataPassing
{
  var dataStore: PickUpCarDataStore? { get }
}

class PickUpCarRouter: NSObject, PickUpCarRoutingLogic, PickUpCarDataPassing
{
  weak var viewController: PickUpCarViewController?
  var dataStore: PickUpCarDataStore?
  
  // MARK: Routing
  
  func routeToshowDateTime(segue: UIStoryboardSegue?)
  {
    if let segue = segue {
        print(segue.destination)
      let destinationVC = segue.destination as! DateTimeViewController
      var destinationDS = destinationVC.router!.dataStore!
        passDataToPicker(source: dataStore!, destination: &destinationDS)
    } else {
      let storyboard = UIStoryboard(name: "DateTime", bundle: nil)
      let destinationVC = storyboard.instantiateViewController(withIdentifier: "DateTimeViewController") as! DateTimeViewController
      var destinationDS = destinationVC.router!.dataStore!
        passDataToPicker(source: dataStore!, destination: &destinationDS)
        navigateToPicker(source: viewController!, destination: destinationVC)
    }
  }
    func routeTopdfviewer(segue: UIStoryboardSegue?) {
        
          if let segue = segue {
              print(segue.destination)
            let destinationVC = segue.destination as! PDFPreviewViewController
            var destinationDS = destinationVC.router!.dataStore!
            passDataToPDF(source: dataStore!, destination: &destinationDS)
          } else {
            let storyboard = UIStoryboard(name: "PDF", bundle: nil)
            let destinationVC = storyboard.instantiateViewController(withIdentifier: "PDFPreviewViewController") as! PDFPreviewViewController
            var destinationDS = destinationVC.router!.dataStore!
              passDataToPDF(source: dataStore!, destination: &destinationDS)
              navigateToPDF(source: viewController!, destination: destinationVC)
          }
    }

  // MARK: Navigation
  
  func navigateToPicker(source: PickUpCarViewController, destination: DateTimeViewController)
  {
    source.show(destination, sender: nil)
  }
  
    func navigateToPDF(source: PickUpCarViewController, destination: PDFPreviewViewController)
    {
      source.show(destination, sender: nil)
    }
  // MARK: Passing data
  
  func passDataToPicker(source: PickUpCarDataStore, destination: inout DateTimeDataStore)
  {
//    destination.name = source.name
    let date = source.currentDate ?? Date()
    destination.currentDate = date
  }
    func passDataToPDF(source: PickUpCarDataStore, destination: inout PDFPreviewDataStore)
    {
        destination.documentData = source.documentData
        destination.pdfName = source.pdfName
    }
}
