import UIKit

protocol SparePartsCarPresentationLogic
{
  func presentSomething(response: SparePartsCar.Something.Response)
    func presentCheckBoxTireSpare(response: SparePartsCar.Something.Response)
}

class SparePartsCarPresenter: SparePartsCarPresentationLogic
{
  weak var viewController: SparePartsCarDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: SparePartsCar.Something.Response)
  {
    let viewModel = SparePartsCar.Something.ViewModel()
    viewController?.displaySomething(viewModel: viewModel)
  }
    
    func presentCheckBoxTireSpare(response: SparePartsCar.Something.Response) {
        let viewModel = SparePartsCar.Something.ViewModel(isTireSpare: response.isTireSpare)
        viewController?.displayCheckBoxTireSpare(viewModel: viewModel)
    }
}
