            let request = RepoCar.Something.Request(deliveryPerson: selectValue)
            self?.interactor?.selectDelivery(request: request)
        }
        
        isFetchList = true
    }
    
    func setValue(to textfield:DropDown , values: [String], didSelected:@escaping (_ selectedText: String, _ index: Int , _ id:Int )->() ){
