import UIKit

@objc protocol SelectInspectionRoutingLogic
{
  func routeToshowPlaceSegue(segue: UIStoryboardSegue?)
    func routeTosinspectionCarSegua(segue: UIStoryboardSegue?)
}

protocol SelectInspectionDataPassing
{
  var dataStore: SelectInspectionDataStore? { get }
}

class SelectInspectionRouter: NSObject, SelectInspectionRoutingLogic, SelectInspectionDataPassing
{
  weak var viewController: SelectInspectionViewController?
  var dataStore: SelectInspectionDataStore?
  
  // MARK: Routing
  
  func routeToshowPlaceSegue(segue: UIStoryboardSegue?)
  {
    if let segue = segue {
      let destinationVC = segue.destination as! PlaceViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToSomewhere(source: dataStore!, destination: &destinationDS)
    } else {
      let storyboard = UIStoryboard(name: "Main", bundle: nil)
      let destinationVC = storyboard.instantiateViewController(withIdentifier: "PlaceViewController") as! PlaceViewController
      var destinationDS = destinationVC.router!.dataStore!
      passDataToSomewhere(source: dataStore!, destination: &destinationDS)
      navigateToSomewhere(source: viewController!, destination: destinationVC)
    }
  }
    func routeTosinspectionCarSegua(segue: UIStoryboardSegue?)
    {
      if let segue = segue {
        let destinationVC = segue.destination as! PickUpCarViewController
        var destinationDS = destinationVC.router!.dataStore!
        passDataToPickUpCar(source: dataStore!, destination: &destinationDS)
      } else {
        let storyboard = UIStoryboard(name: "PickUpCar", bundle: nil)
        let destinationVC = storyboard.instantiateViewController(withIdentifier: "PickUpCarViewController") as! PickUpCarViewController
        var destinationDS = destinationVC.router!.dataStore!
        passDataToPickUpCar(source: dataStore!, destination: &destinationDS)
        navigateToPickUpCar(source: viewController!, destination: destinationVC)
      }
    }

    
    
  // MARK: Navigation
  
  func navigateToSomewhere(source: SelectInspectionViewController, destination: PlaceViewController)
  {
    source.show(destination, sender: nil)
  }
    
    func navigateToPickUpCar(source: SelectInspectionViewController, destination: PickUpCarViewController)
    {
      source.show(destination, sender: nil)
    }
  
  // MARK: Passing data
  
  func passDataToSomewhere(source: SelectInspectionDataStore, destination: inout PlaceDataStore)
  {
    destination.defaultSelectStoreName = source.defaultSelectStoreName
    destination.defaultSelectReceiveName = source.defaultSelectReceiveName
    
    destination.locationList = source.locationList
    destination.plantLocationList = source.plantLocationList
  }
    
    func passDataToPickUpCar(source: SelectInspectionDataStore, destination: inout PickUpCarDataStore)
    {
      //
    }
}
