import UIKit

protocol SignaturePresentationLogic
{
  func presentSomething(response: Signature.Something.Response)
    func presentTitle(response: Signature.Something.Response)
    func getSignatureUIImage(response: Signature.Something.Response)
}

class SignaturePresenter: SignaturePresentationLogic
{
  weak var viewController: SignatureDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: Signature.Something.Response)
  {
    let viewModel = Signature.Something.ViewModel()
    viewController?.displaySomething(viewModel: viewModel)
  }
    
    func presentTitle(response: Signature.Something.Response) {
        let viewModel = Signature.Something.ViewModel(titleName: response.titleName,
                                                      signatureImage: response.signatureImage,
                                                      user: response.user)
        viewController?.displayTitle(viewModel: viewModel)
    }
    
    
    func getSignatureUIImage(response: Signature.Something.Response) {
        let viewModel = Signature.Something.ViewModel(signatureImage: response.signatureImage,
                                                      user: response.user)
        viewController?.getSignature(viewModel: viewModel)
    }
}
