    func navigateToPDF(source: SummaryCarViewController, destination: PDFPreviewViewController)
    {
      source.show(destination, sender: nil)
    }
  
  // MARK: Passing data
  
  //func passDataToSomewhere(source: SummaryCarDataStore, destination: inout SomewhereDataStore)
  //{
  //  destination.name = source.name
