import UIKit

protocol UploadPresentationLogic
{
  func presentUpload(response: Upload.Something.Response)
    func presentProgress(response: Upload.Something.Response)
    func presentUploadView(response: Upload.Something.Response)
    func presentUploadSuccess(response: Upload.Something.Response)
}

class UploadPresenter: UploadPresentationLogic
{
  weak var viewController: UploadDisplayLogic?
  
  // MARK: Do something
  
  func presentUpload(response: Upload.Something.Response)
  {
    if let kError = response.error {
        let message = "Error Code: \(kError.errorCode ?? -1999)"
        let viewModel = Upload.Something.ViewModel(modelPhotoImage : response.modelPhotoImage,
                                                   errorMessage: message)
        viewController?.displayErrorMessage(viewModel: viewModel)
    }else{
        let viewModel = Upload.Something.ViewModel()
        viewController?.displayUploadSuccessByItem(viewModel: viewModel)
    }
  }
    
    func presentProgress(response: Upload.Something.Response) {
        guard let progressModel = response.progressModel  else { return }
        
       
        let viewModel = Upload.Something.ViewModel(progressModel: progressModel)
        viewController?.displayProgressUploads(viewModel: viewModel)
    }
    
    func presentUploadView(response: Upload.Something.Response) {
        guard let uploadList = response.uploadList else { return }
        
        let viewModel = Upload.Something.ViewModel(uploadList : uploadList)
        viewController?.displayCreateProgressView(viewModel: viewModel)
    }
    
    func presentUploadSuccess(response: Upload.Something.Response) {
        if let error = response.error {
            let viewModel = Upload.Something.ViewModel(errorMessage : error.message)
            viewController?.displayErrorUpdateStatus(viewModel: viewModel)
        }else{
            let viewModel = Upload.Something.ViewModel(uploadSuccessMessage : "อัพโหลดสำเร็จ")
            viewController?.displayUploadSuccess(viewModel: viewModel)
        }
        
    }
}
