import UIKit

protocol ImageViewerBusinessLogic
{
  func doSomething(request: ImageViewer.Something.Request)
    func showImageViewer(request: ImageViewer.Something.Request)
}

protocol ImageViewerDataStore
{
  //var name: String { get set }
    var imageViewer:UIImage? { get set }
}

class ImageViewerInteractor: ImageViewerBusinessLogic, ImageViewerDataStore
{
  var presenter: ImageViewerPresentationLogic?
  var worker: ImageViewerWorker?
  //var name: String = ""
  
    var imageViewer:UIImage?
    
  // MARK: Do something
  
  func doSomething(request: ImageViewer.Something.Request)
  {
    worker = ImageViewerWorker()
    worker?.doSomeWork()
    
    let response = ImageViewer.Something.Response()
    presenter?.presentSomething(response: response)
  }
    
    func showImageViewer(request: ImageViewer.Something.Request) {
        let response = ImageViewer.Something.Response(image: imageViewer)
        presenter?.presentImageViewer(response: response)
    }
}
